targets.rawSql:
SELECT COUNT(node_id) 
FROM node_details 
WHERE node_id <> '0';

SELECT COUNT(*) 
FROM node_details 
WHERE updated_at  >= NOW() - INTERVAL 30 MINUTE;

SELECT COUNT(*) 
FROM mesh_packet_metrics 
WHERE source_id IN ($Nodes);

SELECT SUM(message_size_bytes)
FROM mesh_packet_metrics 
WHERE rx_time >= UNIX_TIMESTAMP(NOW() - INTERVAL 1 HOUR) AND source_id IN ($Nodes);
